import React, { Component } from 'react';
import { Link } from "react-router-dom";
import axios from 'axios';
import Hand from './Hand';
import update from 'immutability-helper';
import './blackjack.css';

class Blackjack extends Component {
  constructor(props) {
    super(props);

    this.state = {
      gameStatus: 'Are you ready?',
      deck_id: '',
      playerHand: [],
      dealerHand: [],
      inProgress: true,
      splitHand: [],
      hasSplit: false,
      activeHand: 'playerHand', // 跟踪当前正在处理的手牌
      doubleDown: false, // 标记是否加倍下注
      insuranceActive: false // 标记是否购买了保险
    };

    this.betting = new BlackjackBetting(); // 初始化下注系统
  }

  componentDidMount = () => {
    axios
      .get('https://deckofcardsapi.com/api/deck/new/shuffle/?deck_count=1')
      .then(response => {
        const newState = {
          deck_id: response.data.deck_id
        };

        this.setState(newState, this.deckHasBeenShuffled);
      });
  }

  deckHasBeenShuffled = () => {
    this.dealInitialCards(2, 'playerHand');
    this.dealInitialCards(2, 'dealerHand');
  }

  dealInitialCards = async (numOfCards, whichHand) => {
    await axios
      .get(`https://deckofcardsapi.com/api/deck/${this.state.deck_id}/draw/?count=${numOfCards}`)
      .then(resp => {
        const newState = {
          [whichHand]: resp.data.cards
        };
        this.setState(newState);
      });

    // 检查庄家的明牌是否为A，如果是，则启用保险选项
    if (whichHand === 'dealerHand' && resp.data.cards[0].value === 'ACE') {
      this.setState({ insuranceActive: true });
    }
  }

  componentDidUpdate(prevProps, prevState) {
    if (!this.state.inProgress) {
      return;
    }

    const playerHandTotal = this.totalHand('playerHand');
    const splitHandTotal = this.totalHand('splitHand');
    const { activeHand, hasSplit } = this.state;

    // 确保只有在状态变化时才更新状态
    if (activeHand === 'playerHand' && playerHandTotal > 21 && prevState.activeHand !== 'splitHand') {
      this.setState({
        gameStatus: 'Player Busted!',
        activeHand: hasSplit ? 'splitHand' : activeHand,
        inProgress: hasSplit // 如果有分牌，还在进行中，否则结束
      });
      this.betting.loseBet(); // 玩家爆牌
    } else if (activeHand === 'splitHand' && splitHandTotal > 21 && prevState.activeHand !== 'playerHand') {
      this.setState({
        gameStatus: 'Split Hand Busted!',
        inProgress: false
      });
      this.betting.loseBet(); // 分牌爆牌
    }
  }

  dealCards = async (numOfCards, whichHand) => {
    if (!this.state.inProgress) {
      return;
    }

    await axios
      .get(`https://deckofcardsapi.com/api/deck/${this.state.deck_id}/draw/?count=${numOfCards}`)
      .then(resp => {
        const newState = {
          [whichHand]: update(this.state[whichHand], {
            $push: resp.data.cards
          })
        };
        this.setState(newState);
      });

    // 如果加倍下注，则只能再要一张牌，之后必须停牌
    if (this.state.doubleDown) {
      this.setState({ doubleDown: false });
      this.stand();
    }
  }

  hit = () => {
    this.dealCards(1, this.state.activeHand);
  }

  stand = async () => {
    if (this.state.activeHand === 'playerHand' && this.state.hasSplit) {
      // 如果是主手牌且有分牌，切换到分牌
      this.setState({ activeHand: 'splitHand' });
    } else {
      // 否则，结束游戏并进行结算
      await this.endGame();
    }
  }

  endGame = async () => {
    // 庄家摸牌
    while (this.totalHand('dealerHand') < 17) {
      await this.dealCards(1, 'dealerHand');
    }

    const playerTotal = this.totalHand('playerHand');
    const splitTotal = this.state.hasSplit ? this.totalHand('splitHand') : 0;
    const dealerTotal = this.totalHand('dealerHand');

    let gameStatus = '';

    // 结算主手牌
    if (playerTotal <= 21 && (playerTotal > dealerTotal || dealerTotal > 21)) {
      gameStatus += this.state.doubleDown ? 'Player Wins with Double Down! ' : 'Player Wins! ';
      this.betting.winBet();
    } else if (playerTotal === dealerTotal) {
      gameStatus += 'Player Push! ';
      this.betting.pushBet();
    } else {
      gameStatus += 'Dealer Wins! ';
      this.betting.loseBet();
    }

    // 结算分牌
    if (this.state.hasSplit) {
      if (splitTotal <= 21 && (splitTotal > dealerTotal || dealerTotal > 21)) {
        gameStatus += 'Split Hand Wins! ';
        this.betting.winBet();
      } else if (splitTotal === dealerTotal) {
        gameStatus += 'Split Hand Push! ';
        this.betting.pushBet();
      } else {
        gameStatus += 'Dealer Wins against Split Hand! ';
        this.betting.loseBet();
      }
    }

    // 处理保险
    if (this.state.insuranceActive) {
      if (dealerTotal === 21) {
        this.betting.insuranceWin();
        gameStatus += 'Dealer has Blackjack. Insurance Wins!';
      } else {
        this.betting.insuranceLose();
        gameStatus += 'Dealer does not have Blackjack. Insurance Loses.';
      }
      this.setState({ insuranceActive: false });
    }

    this.setState({
      inProgress: false,
      gameStatus: gameStatus
    });
  }

  getHandResult = (handTotal, dealerTotal, handName) => {
    if (handTotal > 21) {
      return `${handName} Busted! `;
    } else if (dealerTotal > 21 || handTotal > dealerTotal) {
      return `${handName} Wins! `;
    } else if (handTotal < dealerTotal) {
      return `Dealer Wins against ${handName}! `;
    } else {
      return `${handName} Ties with Dealer! `;
    }
  }

  split = () => {
    if (this.betting.splitBet()) {
      const { playerHand } = this.state;
      const splitCard = playerHand.pop();

      this.setState({
        playerHand: [playerHand[0]],
        splitHand: [splitCard],
        hasSplit: true
      }, () => {
        this.dealCards(1, 'playerHand');
        this.dealCards(1, 'splitHand');
      });
    }
  }

  doubleDown = () => {
    if (this.betting.doubleDownBet()) {
      this.setState({ doubleDown: true });
      this.hit();
    }
  }

  placeInsurance = () => {
    this.betting.placeInsurance();
  }

  surrender = () => {
    this.betting.surrender();
    this.setState({
      inProgress: false,
      gameStatus: 'Player Surrendered!'
    });
  }

  switchHand = () => {
    this.setState(prevState => ({
      activeHand: prevState.activeHand === 'playerHand' ? 'splitHand' : 'playerHand'
    }));
  }

  canSplit = () => {
    const { playerHand } = this.state;
    return playerHand.length === 2 && playerHand[0].value === playerHand[1].value;
  }

  totalHand = whichHand => {
    let total = 0;
    let acesCount = 0;
    this.state[whichHand].forEach(card => {
      const values = {
        ACE: 11,
        KING: 10,
        QUEEN: 10,
        JACK: 10
      };
      total += (values[card.value] || parseInt(card.value, 10)); // 确保 parseInt 使用基数 10
      if (card.value === 'ACE') acesCount++;
    });
    while (total > 21 && acesCount > 0) {
      total -= 10;
      acesCount--;
    }
    return total;
  }

  get hideButtons() {
    return this.state.inProgress ? '' : 'hidden';
  }

  _newGame = event => {
    document.location.reload();
  }

  renderHand = (hand, handName, totalClass) => {
    const isActive = handName === this.state.activeHand;
    const titleClass = isActive ? 'active-hand-title' : 'inactive-hand-title';

    // 根据当前激活的手牌确定显示的标题
    const title = this.state.activeHand === 'playerHand' ? 
      (handName === 'playerHand' ? 'Your Cards:' : 'Your Split Cards:') :
      (handName === 'playerHand' ? 'Your Split Cards:' : 'Your Cards:');

    return (
      <div className={`hand-container ${isActive ? 'active-hand' : ''}`}>
        <p className={titleClass}>
          {title}
        </p>
        <p className={`${totalClass} ${titleClass}`}>{this.totalHand(handName)}</p>
        <div className={handName}>
          <Hand cards={hand} />
        </div>
        <div className="split-buttons">
          <button
            className={`split ${this.hideButtons}`}
            onClick={this.split}
            disabled={!this.canSplit() || this.state.hasSplit}
          >
            Split
          </button>
          <button
            className={`switchHand ${this.hideButtons}`}
            onClick={this.switchHand}
            disabled={!this.state.hasSplit}
          >
            Switch
          </button>
          <button
            className={`double-down ${this.hideButtons}`}
            onClick={this.doubleDown}
          >
            Double Down
          </button>
          {this.state.insuranceActive && (
            <button
              className={`insurance ${this.hideButtons}`}
              onClick={this.placeInsurance}
            >
              Insurance
            </button>
          )}
          <button
            className={`surrender ${this.hideButtons}`}
            onClick={this.surrender}
          >
            Surrender
          </button>
        </div>
      </div>
    );
  }

  render() {
    return (
      <div className="blackjack-page">
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
          <div className="container px-5">
            <a className="navbar-brand" href="index.html">React Blackjack</a>
            <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation"><span className="navbar-toggler-icon"></span></button>
            <div className="collapse navbar-collapse" id="navbarSupportedContent">
              <ul className="navbar-nav ms-auto mb-2 mb-lg-0">
                <li className="nav-item"><Link className="nav-link" to="/">Home</Link></li>
                <li className="nav-item"><Link className="nav-link active" to="/blackjack">Play</Link></li>
              </ul>
            </div>
          </div>
        </nav>

        <h1 className="top-section"> </h1>
        <div className="center">
          <p className="game-status">{this.state.gameStatus}</p>
        </div>
        <div className="center">
          <button className="reset hidden">Play Again!</button>
        </div>

        <div id="betting-container">
          <div id="balance">Balance: $1000</div>
          <div id="current-bet">Current Bet: $0</div>
          <div id="controls">
            <input type="number" id="bet-amount" placeholder="Enter bet amount" />
            <button id="place-bet">Place Bet</button>
          </div>
          <div id="controls">
            <button id="win">Win</button>
            <button id="blackjack-win">Blackjack Win</button>
            <button id="push">Push</button>
            <button id="lose">Lose</button>
            <button id="split">Split</button>
            <button id="double-down">Double Down</button>
            <button id="insurance">Insurance</button>
            <button id="surrender">Surrender</button>
          </div>
        </div>

        <div id="blackjack-container">
          <div className="game-area">
            <div className="left">
              <button className={`hit ${this.hideButtons}`} onClick={this.hit}>
                Hit
              </button>
              {this.state.hasSplit ? (
                <>
                  {this.state.activeHand === 'playerHand' ? (
                    <>
                      {this.renderHand(this.state.playerHand, 'playerHand', 'player-total')}
                      {this.renderHand(this.state.splitHand, 'splitHand', 'split-total')}
                    </>
                  ) : (
                    <>
                      {this.renderHand(this.state.splitHand, 'splitHand', 'split-total')}
                      {this.renderHand(this.state.playerHand, 'playerHand', 'player-total')}
                    </>
                  )}
                </>
              ) : (
                this.renderHand(this.state.playerHand, 'playerHand', 'player-total')
              )}
            </div>

            <div className="right">
              <button className={`stand ${this.hideButtons}`} onClick={this.stand}>
                Stand
              </button>
              <p className="active-hand-title">Dealer Cards:</p>
              <p className="dealer-total active-hand-title">{this.totalHand('dealerHand')}</p>
              <div className="dealer-hand">
                <Hand cards={this.state.dealerHand} />
              </div>
            </div>
          </div>
        </div>

        <div className="new-game">
          <button onClick={this._newGame} className="reset">
            New Game
          </button>
        </div>
      </div>
    );
  }
}

export default Blackjack;
